const vec3 WHITE = vec3(0.,1.,0.);
const vec3 BLACK = vec3(0.,0.,0.);
const vec3 BACK = vec3(.3,.3,.3);
const int LEN = 5;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 v = fragCoord.xy;
	vec2 s = iResolution.xy;
	float size = min(s.x, s.y);
	v = v - vec2(s.x - size, s.y - size) / 2.;
	s = vec2(size);
	vec3 col;
	if(all(greaterThan(s, v)) && all(greaterThan(v, vec2(0., 0.)))){
		col = BLACK;
		for(int i=0; i<LEN; i++){
			s /= 3.;
			v = mod(v, s * 2.);
			if(all(greaterThan(v, s))){
				col = WHITE;
				break;
			}
			v = mod(v, s);
		}
	}else
	   col = BACK;
	fragColor = vec4(col, 1.);
}
