float propeller(vec2 uv, vec2 center, float propWidth, float size, float numPropellers, float phase) {
	vec2 uvP = uv - center;
	// polar coordinates
	float r = length(uvP);
	float th = atan(uvP.y, uvP.x);	

	float angle = numPropellers*th + phase;
	// function of angle
	float f = (sin(angle)+1.0)*(1.0 - smoothstep(size, size+0.3, r));
	f = smoothstep(propWidth, propWidth+0.1, f);	

	return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-0.5*iResolution.xy) / iResolution.y;
	float t = iTime;

	float pCenter = propeller( uv, vec2(0.0, 0.0), 1.4, 0.3, 5.0, pow(t, 5.0));
	float pTopLeft = propeller( uv, vec2(-0.6, 0.25), 1.8, 0.08, 1.0, t);
	float pTopRight = propeller( uv, vec2(0.6, 0.25), 1.5, 0.05, 2.0, pow(t, 2.0));
	float pBottomLeft = propeller( uv, vec2(-0.6, -0.25), 1.5, 0.05, 3.0, pow(t, 3.0));
	float pBottomRight = propeller( uv, vec2(0.6, -0.25), 1.1, 0.02, 4.0, pow(t, 4.0));

	float propellers = pCenter + pTopLeft + pTopRight + pBottomLeft + pBottomRight;

	vec3 bgColor = vec3(52.,152.,219.)/255.;
	vec3 propColor = vec3(231., 76., 60.)/255.;
	vec3 col = mix(bgColor, propColor, propellers);

	col -= vec3(1.0)*smoothstep(0.3, 2.9, length(uv)); // vignette
	fragColor = vec4(col, 1.0);
}
