#define PI 3.1415

float eye(vec2 c, vec2 uv)
{
	return clamp(-log(distance(uv, c) * (35.0 + sin(iTime  * 200.0) * 5.0)), 0.0, 1.0);
}

float curve(float x, float m)
{
	return 1.0 - sin(x * PI) * m;
}

float mouth(vec2 c, vec2 uv, float d)
{
	float x = c.x + uv.x * 8.0;
	float y = c.y + uv.y * 32.0;
	return float(abs(x) < 1.0) * float(y > curve(x, 1.0) && y < curve(x, d));
}

float teeth(vec2 c, vec2 uv, float d)
{
	float x = c.x + uv.x * 8.0;
	float y = c.y + uv.y * 32.0;
	return float(abs(x - 0.5) < 0.5) * float(abs(y - curve(x, d)) < 0.02);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv = (uv * 2.0 - vec2(1.0)) * vec2(iResolution.x / iResolution.y, 1.0);

	vec2 polar = vec2(atan(uv.x, -uv.y) / PI, length(uv));
	polar.y = 1.0 / polar.y / 3.0;

	float delta = clamp(1.0 - abs(polar.x - 0.0) * 4.0, 0.0, 1.0);


	vec4 pipe = texture(iChannel0, polar) * clamp(0.0, 1.0, 4.0 - polar.y);
	vec4 blood = texture(iChannel1, polar) * clamp(0.0, 1.0, 5.0 - polar.y) * vec4(1.0, 0.0, 0.0, 1.0) + vec4(0.5, 0.0, 0.0, 0.0);


	fragColor = mix(pipe / polar.y * 0.15, blood / polar.y * 0.3 * 0.4, delta);
	fragColor += eye(vec2(0.03, 0.03), uv) * vec4(1.0, 0.0, 0.0, 1.0);
	fragColor += eye(vec2(-0.03, 0.03), uv) *  vec4(1.0, 0.0, 0.0, 1.0);
	fragColor += mouth(vec2(0.5, 1.12), uv, 0.5) * vec4(1.0) * 0.85;
	fragColor -= teeth(vec2(0.5, 1.12), uv, 0.75) * vec4(1.0);
}
