void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec4 m_texture;	
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec2 point = -1.0 + 2.0*fragCoord.xy/iResolution.xy;
    point.x*=1.4;

	float r = length(point);
	float a = atan(point.y,point.x);

	uv.x = 1.0/r +3.0;
	uv.y = a*3.0/3.14159265359;
	uv.xy+=iTime/3.;

	m_texture = texture(iChannel0,uv*2.);
	m_texture   /= r *1.6;
    m_texture.z /= r *.85;

	fragColor = m_texture;
}
