void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.yy;

    vec2 ballCenter = vec2(0.5,0.5);
    float radius = 0.2;
	float xCoord = 1.6*abs(mod(iTime/1.,2.0)-1.0);
  	float yCoord = radius+0.3*pow(abs(sin(iTime*3.6)),1.);


    float yCoordL = radius+0.3*pow(abs(sin((iTime-1./60.0)*3.6)),1.);


    float yV = abs(yCoord - yCoordL)*10.0;

    float zCoord = abs(mod(iTime/3.,2.0)-1.0);

    float ballDepth = 1.6+4.*zCoord;



    ballCenter[1] = yCoord;
    ballCenter[0] = xCoord;


    float yThreshold = 0.5+0.15*sin(iTime*0.15);

    float rOut= (mod(1./(-abs(uv[1]-yThreshold))/5.-iTime*1.2,1.));
    //let's fog it off so that we're not getting the horrible moire.



    rOut = step(rOut,0.15);
    rOut  = rOut*3.*abs(uv[1]-yThreshold);

    float gOut =  uv[1]>yThreshold?.2:.7;
    gOut = gOut*3.*(abs(uv[1]-yThreshold)+0.07);

        vec3 clr = vec3(rOut,
                       gOut,
                        0.5+0.15*sin(iTime*10.));



        float dx = abs(uv[0]-ballCenter[0])/radius;

        float dy = (uv[1]-0.12)*9.5;
        float dist = sqrt(dy*dy+dx*dx)*ballDepth/(yCoord+0.8);

    	clr = mix(vec3(0.0),clr,clamp(dist*1.,0.4,1.));       


   	dy = (uv[1]-ballCenter[1]);
   	dx = (uv[0]-ballCenter[0])*(1.+.14*yV);

    dist = sqrt(dy*dy+dx*dx);
    if(dist<radius/ballDepth) {

        //let's do something more interesting with the ball's shading.
        dy = (uv[1]-ballCenter[1]-0.03);
        dx = (dx)*0.9;
    	dist = sqrt(dy*dy+dx*dx);


        dist*=9.;
        dist = pow(dist,.60);
 		clr = mix(vec3(0.0,0.3,0.4),vec3(1.),1.-dist);

    }


	fragColor = vec4(clr,1.0);
}
