float f(float x)
{
	return .3*sin(x/cos(mod(iTime/100.+250.,190./pow(x, 4.))))*
        cos(x*mod(iTime/100.+150.,pow(x, 4.)))*
        exp(x/mod(iTime/100.+150., x*x*x*x*x));
}

bool cmp(float a, float b, float epsilon)
{
	return (abs(a-b))<epsilon;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 p = fragCoord.xy / iResolution.xy * 8.0 - 4.;
	vec2 plot = fragCoord.xy / iResolution.xy;

	float size = mix(0.25, 0.3, sin(iTime*5.));

	if(cmp((p.x+p.y)/2., f((p.x-p.y)/2.), size))
		fragColor = vec4(0.0, size*1.75, 1.0, 1.);

	else fragColor = vec4(0.0);

}
