const float rings = 20.0;
const float spin = 0.3;

vec2 rotate (vec2 coord, float angle){
    float sin_factor = sin(angle);
    float cos_factor = cos(angle);
	vec2 c = vec2((coord.x - 0.5) * (iResolution.x / iResolution.y), coord.y - 0.5) * mat2(cos_factor, sin_factor, -sin_factor, cos_factor);
    c += 0.5;
    return c;
}


float rand(float co){
    return fract(sin(co * 12.9898) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 res = iResolution.xy;
	vec2 uv = fragCoord.xy ;
    vec2 tc = uv / res;
    float aspect = res.x / res.y;

    tc.y = 1.0 - tc.y;

    float d =  floor(distance(vec2(0.5 * aspect, 0.5),vec2(tc.x * aspect, tc.y))*rings)/rings;
    d += 0.001;
	vec4 t = texture(iChannel0, rotate(tc,   d*spin + iTime*0.1 * rand(d)));


	fragColor =  t;
}
