void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 color = vec4(0.);

    vec2 center = vec2(0.5, 0.5); 

    //fragColor=vec4(p,0.,1.);


        float t = iTime * 0.075;

        float cloud1 = texture(iChannel1, uv + vec2(t, t)).r + 0.1 * sin(t * 10.0);
        float cloud2 = texture(iChannel1, 0.7 * uv + vec2(-t, t)).r - 0.1 * sin(t * 10.0);

        float cloud3 = mix(cloud1, cloud2, 0.5);

        float cloud = step(0.6, cloud3) * 0.6;
        if (cloud < 0.01) 
            cloud = step(0.4, cloud3) * 0.4;
        if (cloud < 0.01) 
            cloud = step(0.3, cloud3) * 0.2;  

        fragColor=vec4(cloud);
}
