const int NUM_BALLS = 4;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 balls[NUM_BALLS];
	vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / min(iResolution.y, iResolution.x);
    float sum = 0.0;

    balls[0] = vec2(sin(iTime * 0.3), cos(iTime * 0.75)) * 0.8;
    balls[1] = vec2(cos(iTime * 0.7), sin(iTime)) * 0.8;
    balls[2] = vec2(sin(iTime), cos(iTime * (1.0 / 3.0))) * 0.8;
    balls[3] = vec2(cos(iTime * 0.4), cos(iTime * 0.5)) * 0.8;

    for(int i = 0; i < NUM_BALLS; i++)
        sum += 0.25 / mix(abs(uv.x - balls[i].x) + abs(uv.y - balls[i].y), length(uv - balls[i]), (cos(iTime + float(i) * 13.5) + 1.0) * 0.5);

    sum /= float(NUM_BALLS);

    fragColor = vec4(smoothstep(smoothstep(0.6, 0.4, sum), sum, (sin(iTime) + 1.0) * 0.5));
}
