#define PI 3.141

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    // swap axis to move the line down
    uv.xy = uv.yx;

    // move uv to center of screen
    vec2 p = uv * 2. - 1.;

    // use atan2 to get the direction
	float dir = (atan(p.y, p.x) + PI) / (2.*PI);

	fragColor = vec4(vec3(dir),1.0);
}
