/** 
 * Shadows and Highlights
 * Demo: https://www.shadertoy.com/view/4sjfW3
 * starea @ ShaderToy
 * 
 * Forked and remixed from: 
 * [0] mAlk's Bloom: https://shadertoy.com/view/Ms2Xz3
 * [1] tr4racer's vibrance: https://shadertoy.com/view/llGSzK
 * [2] https://shadertoy.com/view/MsjXRt
 *
 * refer to https://www.shadertoy.com/view/MdjBRy for brightness and contrast
 *
 * Created 8/11/2017
 * Updated 8/11/2017
 **/

vec4 shadows( vec4 col, in float mix )
{
    if( mix < 1.0)
    {
        col = ((2.0 * col - (4.652*col - 23.87812*col*col + 65.47231*col*col*col - 89.41696*col*col*col*col + 59.70575*col*col*col*col*col - 15.53498*col*col*col*col*col*col))* (1.0 - mix) + col * mix);
        clamp(col,0.0,1.0);
    }
    else
    {
        if (mix > 2.0) mix = 2.0;
        mix = 2.0 - mix;
        col = ((4.652*col - 23.87812*col*col + 65.47231*col*col*col - 89.41696*col*col*col*col + 59.70575*col*col*col*col*col - 15.53498*col*col*col*col*col*col) * (1.0 - mix) +col * mix);
    }
    return vec4(col.x, col.y, col.z, 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float shadows_0 = 1.0 + 5.0*cos(iTime);
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 pixel = texture(iChannel0, uv);
	fragColor = shadows(pixel, shadows_0);
}
