void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 Offset = vec2(1.0/iResolution.x, 1.0/iResolution.y);

    vec4 color = vec4(0.0);

    if(uv.x > 0.7)
    {
        color = texture(iChannel1, uv);
    }
    else if(uv.x > 0.3)
    {
        color = texture(iChannel0, uv);
    }
    else
    {
        color += texture( iChannel0, uv + vec2( -3.0*Offset.x, -3.0*Offset.y ) ) * 0.015625;
        color += texture( iChannel0, uv + vec2( -2.0*Offset.x, -2.0*Offset.y ) ) * 0.09375;
        color += texture( iChannel0, uv + vec2( -1.0*Offset.x, -1.0*Offset.y ) ) * 0.234375;
        color += texture( iChannel0, uv + vec2( 0.0 , 0.0) )*0.3125;
        color += texture( iChannel0, uv + vec2( 1.0*Offset.x,  1.0*Offset.y ) ) * 0.234375;
        color += texture( iChannel0, uv + vec2( 2.0*Offset.x,  2.0*Offset.y ) ) * 0.09375;
        color += texture( iChannel0, uv + vec2( 3.0*Offset.x, -3.0*Offset.y ) ) * 0.015625;

    }



    fragColor = color;
}

