void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0,0.0,1.0,1.0);

    vec2 uv = fragCoord.xy / iChannelResolution[0].xy;
    vec2 stepSize = vec2(1./iChannelResolution[0].x,0.);

    const int r = 6;
    // Box filter x
    vec4 sum = vec4(0.,0.,0.,0.);
    for(int i = -r; i <= r; i++)
    {
        sum += texture(iChannel0,uv+float(i)*stepSize);
    }

    fragColor = sum/float(2*r+1);
}

