bool intpoint_inside_trigon( in vec2 s, in vec2 a, in vec2 b, in vec2 c)
{
    float as_x = s.x-a.x;
    float as_y = s.y-a.y;

    bool s_ab = (b.x-a.x)*as_y-(b.y-a.y)*as_x > 0.0;

    if((c.x-a.x)*as_y-(c.y-a.y)*as_x > 0.0 == s_ab) return false;

    if((c.x-b.x)*(s.y-b.y)-(c.y-b.y)*(s.x-b.x) > 0.0 != s_ab) return false;

    return true;
}

vec2 project_vec3( in vec3 coords3, in vec3 camrot3, in vec3 rot3, in vec3 campos3, in float fs)
{   
    float xin = coords3.x;
    float yin = coords3.y;
    float zin = coords3.z;
    float rc_x = camrot3.x;
    float rc_y = camrot3.y;
    float rc_z = camrot3.z;
    float r_x = rot3.x;
    float r_y = rot3.y;
    float r_z = rot3.z;
    float cam_x = campos3.x;
    float cam_y = campos3.y;
    float cam_z = campos3.z;

    float x2 = (((xin - rc_x) * cos(r_y) - (zin-rc_z) * sin(r_y)) * cos(r_z) - ((yin - rc_y) * cos(r_x) - ((xin - rc_x) * sin(r_y) + (zin - rc_z) * cos(r_y)) * sin(r_x)) * sin(r_z) - cam_x) * (fs / (fs + ((yin - rc_y) * sin(r_x) + ((xin - rc_x) * sin(r_y) + (zin - rc_z) * cos(r_y)) * cos(r_x) - cam_z)));
	float y2 = (((xin - rc_x) * cos(r_y) - (zin-rc_z) * sin(r_y)) * sin(r_z) - ((yin - rc_y) * cos(r_x) - ((xin - rc_x) * sin(r_y) + (zin - rc_z) * cos(r_y)) * sin(r_x)) * cos(r_z) - cam_y) * (fs / (fs + ((yin - rc_y) * sin(r_x) + ((xin - rc_x) * sin(r_y) + (zin - rc_z) * cos(r_y)) * cos(r_x) - cam_z)));

    vec2 coords2 = vec2(x2+(iResolution.x/2.0), y2+(iResolution.y/2.0));
    return coords2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//fragColor = vec4(sin(iTime*2.0),sin(iTime*3.0),sin(iTime*5.0),1.0)*0.5+vec4(0.5,0.5,0.5,0.5);
    vec3 campos = vec3(0.0,0.0,0.0);
    vec3 camrot = vec3(0.0,0.0,0.0);
    vec3 rot = vec3(0.0,0.0,0.0);
    if(iTime > 1.0) rot = vec3(iTime-1.0,(iTime-1.0)*1.4,0.0);

    vec2 point1 = project_vec3(vec3(iResolution.x/12.0,iResolution.y/-12.0,0),camrot,rot,campos,240.0);
    vec2 point2 = project_vec3(vec3(iResolution.x/-12.0,iResolution.y/-12.0,0),camrot,rot,campos,240.0);
    vec2 point3 = project_vec3(vec3(0,iResolution.y/8.0,0),camrot,rot,campos,240.0);

    fragColor = vec4(0.0,0.0,0.0,0.0);
    if(intpoint_inside_trigon(fragCoord, point1, point2, point3)) fragColor = vec4(1.0,1.0,1.0,1.0);

}
