// inspired from https://www.shadertoy.com/view/4scGWf

void mainImage( out vec4 O,  vec2 U )
//{  O += .1/ fract( sin(1e5*length (ceil(U/=8.))+iDate.w) < 0.  ? U.x : U.y ) - O; }

{   float t =  sin(1e5*length (ceil(U/=24.))+iDate.w); // random cell-wall orientation
 // t = clamp(5.*t,-1.,1.);     // faster switch
    t = atan(20.*t)/atan(20.);  // faster switch
    O += .1/ ( abs(dot(fract(U),cos(.78*(t+1.+vec2(0,2))) ))) - O; // draw cell-wall
}
