// Line Segment "Wave" written 2015 by JT

void mainImage( out vec4 o, in vec2 I )
{
    I = 2. * I / iResolution.xy - 1.;
    o -= o;
    vec4 Q = 2. * abs(2. * fract(iTime * vec4(.0432, .0123, .0257, .0332)) - 1.) - 1.;
    Q.wz -= Q.xy;
    o += cos(length(I - (Q.xy + Q.wz * clamp(dot(I - Q.xy, Q.wz) / dot(Q.wz, Q.wz), 0., 1.)))*25.);
}
