#define AA 3

float hash(vec2 n)
{
 	return fract(cos(n.x*.331+n.y*.845)*956.75);   
}
float fractal(vec2 n,float t)
{	
    float ft = floor(t);
    float total = 0.;

    for(float i = 0.;i<8.;i++)
    {
        float w = 1.-(i+ft-t)/7.;
        total += (hash(floor(n/exp2(i+ft-t)+i+ft))-total)*w*w;
    }
 	return total*total;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = fragCoord-iResolution.xy/2.;
    float total = 0.;
    for(int a = 0;a<AA*AA;a++)
    {
        vec2 c = center+vec2(a%AA,a/AA)/float(AA);
        total += fractal(c/8.,iTime*.5);
    }
    total /= float(AA*AA);

	fragColor = vec4(total);
}
