#define PI 3.14159
const float it = 5.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time = iTime;
	float mx = max(iResolution.x, iResolution.y);
	vec2 scrs = iResolution.xy/mx;
	vec2 uv = vec2(fragCoord.x, iResolution.y-fragCoord.y)/mx;
	vec2 m = vec2(iMouse.x/mx,scrs.y-iMouse.y/mx);

	uv+=m;
	float v = pow(3.0,it)+100.0;

	fragColor = vec4(vec3(0.0),1.0); // Background color

	for (float i = 0.0; i < it; i++)
	{
		if(floor(mod(uv.x*v,3.0))==1.0 && floor(mod(uv.y*v,3.0))==1.0){

			fragColor = vec4(((sin(i*5.0-time*0.5+2.0*PI/3.0)+1.0))/2.0, // RED
					    	 ((sin(i*5.0-time*0.5+4.0*PI/3.0)+1.0))/2.0, // GREEN
					    	 ((sin(i*5.0-time*0.5+6.0*PI/3.0)+1.0))/2.0, // BLUE
					    	 1.0);  									 // ALPHA
		}
		v/=3.0;	
	} 
}
