#define PI 3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time = iTime;
	float mx = max(iResolution.x, iResolution.y);
	vec2 scrs = iResolution.xy/mx;
	vec2 uv = vec2(fragCoord.x, iResolution.y-fragCoord.y)/mx;
	vec2 m = vec2(iMouse.x/mx,scrs.y-iMouse.y/mx);

    if(iMouse.z == 0.0){ // starts with mouse position in the center of the screen
        m = scrs/2.0;
    }


	vec4 sinWave = vec4((1.0-distance(uv.y,scrs.y*0.5+sin(distance(uv.x,scrs.x)*30.0)*0.05)*70.0) * distance(sin(uv.x*50.0+2.0*PI/3.0-time*5.0),scrs.x),
						(1.0-distance(uv.y,scrs.y*0.5+sin(distance(uv.x,scrs.x)*30.0)*0.05)*70.0) * distance(sin(uv.x*50.0+4.0*PI/3.0-time*5.0),scrs.x),
						(1.0-distance(uv.y,scrs.y*0.5+sin(distance(uv.x,scrs.x)*30.0)*0.05)*70.0) * distance(sin(uv.x*50.0+6.0*PI/3.0-time*5.0),scrs.x),
						 1.0);

    fragColor = sinWave + vec4(smoothstep(0.2,0.0,distance(uv,m)))*1.3;	
}
