void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv *= 2.3;

    float divs = 5.;
    //float slope = (sin(iTime)+1.)*2.+1.5;
    float slope = 4.2;
    float y_off = sin(2.*uv.y+iTime/1.73);
    float vy = uv.y+sin(uv.x*6.28318 + y_off)*.1 -.05;

    float c = (smoothstep(0., 1., mod(vy*divs, 1.0)*slope) + floor(vy*divs))/divs;
    vec2 uv_d = vec2(uv.x*0.3 + y_off*.01, c);

    vec2 uv_d2 = vec2(uv.x + y_off*.05, .3*c+.7*vy);

    vec3 gamma = vec3(0.65*2.2, .8*2.2, 2.2);
    fragColor.r = pow(c, 1./gamma.r);
    fragColor.g = pow(c, 1./gamma.g);
    fragColor.b = pow(c, 1./gamma.b);
    fragColor.a = 1.;

    fragColor *= vec4(1.0, 0.95, 0.9, 1.0) * texture(iChannel0, uv_d).r * (texture(iChannel1, uv_d2*2.).r*.3+.7);
}
