float sigmoid(float x) 
{
	float expx = exp(x);
    return expx/(expx+1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = ( 2.*fragCoord - iResolution.xy ) / iResolution.x;

    float angle = atan(xy.y, xy.x);
    if (abs(angle) < 3.1415/2.0)
    {
    	angle += sign(angle)*3.1415/2.0;
    }
    angle = pow(abs(angle), 4.0);

    vec2 pattern = vec2(mod(angle,
                            pow(abs(sigmoid(sin(length(xy)*(3.0+sin(iTime*0.3))+1.0))-1.0), 3.0)
                           ))*20.0;
    float s = pow(1.0 - length(pattern), 2.0);
    float d = min(1./length(xy), 1.0);
    vec3 col = vec3(
        sigmoid(0.3*d)*s,
        sigmoid(0.2*d)*s,
        sigmoid(0.1*d)*s);

    fragColor = vec4(col, 1.0);
}
