//	added color and initialization to https://www.shadertoy.com/view/lddGWj -- can use the mouse

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	fragColor = texture(iChannel0,fragCoord/iResolution.xy);
}

#define T true
#define F false
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec4 b = texture(iChannel0,fragCoord/iResolution.xy);
    float r = fract( 1e5 * sin( dot( fragCoord, vec2( 7.1, 9.1 ) ) ) );
    bool a  = bool(b.r);
    vec4 c = texture(iChannel0,(fragCoord+vec2( 0, 1))/iResolution.xy)+
             texture(iChannel0,(fragCoord+vec2( 0,-1))/iResolution.xy)+
             texture(iChannel0,(fragCoord+vec2( 1,-1))/iResolution.xy)+
             texture(iChannel0,(fragCoord+vec2( 1, 0))/iResolution.xy)+
             texture(iChannel0,(fragCoord+vec2( 1, 1))/iResolution.xy)+
             texture(iChannel0,(fragCoord+vec2(-1,-1))/iResolution.xy)+
             texture(iChannel0,(fragCoord+vec2(-1, 0))/iResolution.xy)+
             texture(iChannel0,(fragCoord+vec2(-1, 1))/iResolution.xy);
	int n = int(c.r);
    b.gb = a ? b.gb * .99 : vec2( 1 );
    a = a?n>3?F:n<2?F:T:n==3?T:F || distance(fragCoord,iMouse.xy)<9.&&iMouse.z>0. || iFrame == 0 && r < .05;
    fragColor = float( a ) * vec4( 1, b.gb, 1 );
}
