void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 mp = iMouse.xy / iResolution.xy;

    fragColor = texture(iChannel0,uv) * texture(iChannel1,uv);
}

// Created by Charles Humphrey 06/01/2016
// Color and pen size can be changed here.

vec2 penSize = vec2(8,8);
vec4 paintColor = vec4(1,1,1,1);
float fadeOutSpeed = 0.999;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    penSize /= iResolution.xy;
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 mp = iMouse.xy / iResolution.xy;

    // Idea from FabriceNeyret2
    fragColor = fadeOutSpeed * texture(iChannel0,uv);

    if(iMouse.z >= .99 && (mp.x > uv.x-penSize.x && mp.x < uv.x+penSize.x && mp.y > uv.y-penSize.y && mp.y < uv.y +penSize.y))
    	fragColor = paintColor;
}
