void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

vec4 noise(vec2 uv, vec2 texel) {
    vec2 uvN = vec2(uv.x*3. + iTime * 3.1415, uv.y - iTime * 3.1415);
    return texture(iChannel1, uvN);
}

vec2 coord(vec2 uv, vec2 texel, float x, float y) {

    return uv + vec2(texel.x * x, texel.y * y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 texel = 1. / iResolution.xy;
    vec2 uv = fragCoord.xy / iResolution.xy;


    vec2 wind = vec2(texel.y*sin(iTime)*.2, texel.x*-2.);

    float s = texture(iChannel0, coord(uv, texel, 0., -1.)+wind).x;
    float n = texture(iChannel0, coord(uv, texel, 0., 1.)+wind).x;
    float e = texture(iChannel0, coord(uv, texel, 1., 0.)+wind).x;
    float w = texture(iChannel0, coord(uv, texel, -1., 0.)+wind).x;

    float c = (1. - texture(iChannel2, uv).x) * 0.02;
    float p = (s + n + e + w) / 4.;

    p = p - c;

    if (p < 0.) p = 0.;

    fragColor = mix(vec4(p,p,p,1.), noise(uv, texel), 0.025);

}
