vec2 R(vec2 a, float t)
{
    float tt =iTime;
    vec2 c = vec2(0.5,0.5)+vec2(sin(tt),cos(tt))*.25;
    a -= c;
    return vec2(cos(t)*a.x+sin(t)*a.y,-sin(t)*a.x+cos(t)*a.y) + c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iChannelResolution[0].xy;
    vec4 feedback = texture(iChannel0,R(uv,0.12345*3.14159*.875)*(1.+0.1 * sin(iTime*.5)));
    vec4 new = texture(iChannel1,uv);

    float amount = 0.1*uv.x*(sin(iTime*10.)*0.5+0.5);

    fragColor = mix(feedback,new,amount);
}
