void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = fragCoord.xy;
    xy.x = xy.x / iResolution.x;
    xy.y = xy.y / iResolution.y;

    vec4 rusFlag = vec4(abs(sin(iTime)),0.0,0.0,abs(sin(iTime)));
    if(xy.y > 0.33) {
        rusFlag = vec4(0.0,0.0,abs(sin(iTime)),abs(sin(iTime)));
    }
    if(xy.y > 0.66) {
        rusFlag = vec4(abs(sin(iTime)),abs(sin(iTime)),abs(sin(iTime)),abs(sin(iTime)));
    }

	fragColor = rusFlag;
}
