void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 uv1 = uv;
    vec2 uv2 = uv;


    float y  = mod(floor(uv1.y * iResolution.y / 2.0), 2.0);
    float y1 = mod(floor(0.2-uv2.y * iResolution.y / 2.0), 2.0);


    float x1 =abs(sin((uv1.y-uv1.x + iTime))) * 0.04;
      if (y1 == 0.0 )
       fragColor =  texture(iChannel0, 0.80*(uv2 + vec2(-x1+0.10, 0.)));
    else if(y==0.0)
	   fragColor =  texture(iChannel0, 0.80*(uv1 + vec2(x1+0.10, 0.)));


    vec3 Color;

    vec2 kp = uv;	 

    float ay=max(0.1,0.5-iTime/8.);

    if(kp.y<ay || kp.y>1.-ay+0.006) {fragColor =vec4(0.20,0.27,0.45,1.0);}


} 


