void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 fColor = vec3(1.0);

	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = 2.0 * uv - 1.;
    uv.x *= iResolution.x/ iResolution.y;

    vec2 pos = vec2(.0);

    for(float i = 20.0; i > 0.; --i)
    {
        pos = vec2(sin(iTime+(20.0 - i)*0.4)*0.02, sin(iTime+(20.0 - i)*0.4)*0.02);
    	float dist = length( pos - uv) - 0.05 * i;
    	float o = 1. - smoothstep(0.0,5.0/iResolution.x, dist);

        fColor = mix(fColor , vec3(1.)* (1. -mod(i,2.)),o);
    }

	fragColor = vec4(fColor,1.0);
}
