void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(texture(iChannel0, uv).x, texture(iChannel1, uv).x, texture(iChannel2, uv).x, 1.0)*0.5+0.5;
}

#define WAVE_SPEED 0.1
#define DAMPING 1.0

float laplacianX(sampler2D channel, vec2 fragCoord)
{
    float laplacian = 0.0;
#   define S(DX, DY, WEIGHT) laplacian += texture(channel, (fragCoord+vec2(DX, DY))/iResolution.xy).x*WEIGHT;
    S( 0,  0, -1.0)
    S( 0,  1,  0.2)
    S( 0, -1,  0.2)
    S( 1,  0,  0.2)
    S(-1,  0,  0.2)
    S( 1,  1,  0.05)
    S( 1, -1,  0.05)
    S(-1,  1,  0.05)
    S(-1, -1,  0.05)
#   undef S
    return laplacian;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 prevColor = texture(iChannel0, uv);
    if (iFrame < 32) prevColor = vec4(texture(iChannel1, uv).x, 0.0, 0.0, 0.0);
    float displ = prevColor.x;
    float delta = prevColor.z * DAMPING;
    delta += laplacianX(iChannel0, fragCoord)*WAVE_SPEED;
    fragColor = vec4(displ+delta, 0.0, delta, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
	//fragColor = texture(iChannel0, uv);
    fragColor = vec4(exp(-uv.x*uv.x*100.0));
}
