#define tex iChannel0

void biLinearTex2D( out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;				
	vec2 res = vec2(4.0);
    vec2 st = uv * res - 0.5;

    vec2 iuv = floor( st );
    vec2 fuv = fract( st );

    vec4 a = texture( tex, (iuv+vec2(0.5,0.5))/res );
    vec4 b = texture( tex, (iuv+vec2(1.5,0.5))/res );
    vec4 c = texture( tex, (iuv+vec2(0.5,1.5))/res );
    vec4 d = texture( tex, (iuv+vec2(1.5,1.5))/res );

    fragColor = mix(
        mix( a, b, fuv.x),
        mix( c, d, fuv.x), fuv.y
    );

}

void regularTex2D( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture( tex, uv );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    if (uv.x>0.5)
    	biLinearTex2D(fragColor, fragCoord);
    else
    	regularTex2D(fragColor, fragCoord);
}

#define DIVS 4.0
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 col = floor(uv * DIVS) / DIVS;
	fragColor = vec4( vec3(length(col)) , 1.0 );

}
