void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 30.0 + iTime / 3.;

    // convert the screen coordinates into something more mathy
	vec2 uv = zoom * ((fragCoord.xy - (iResolution.xy / 2.0)) / iResolution.y);

    float breathe = iTime;
    float value = breathe + (uv.x * sign(uv.y) + uv.y * sign(uv.x)) * sin(uv.x) * sin(uv.y);

    float color = sin(value);
    color = color * sign(uv.x) * sign(uv.y); // alternate colors by corner


    // this is not the cleanest way to
    // make the white-blue-black-red-white gradient
    // but it works
    color = color * 3.;
    float low = abs(color);
    float med = abs(color) - 1.0;
    float high = abs(color) - 2.0;
    if(color > 0.0) {
	  fragColor = vec4(med, high, high,1.0);
    } else {
      fragColor = vec4(high, high, med,1.0);
    }
}
