void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 metaballs[4];

	metaballs[0] = vec2(cos(iTime) * 200.0 + 300.0, sin(iTime) * 150.0 + 200.0);
	metaballs[1] = vec2(cos(iTime) * 250.0 + 350.0, sin(iTime * 0.25) * 100.0 + 200.0);
	metaballs[2] = vec2(cos(iTime * 0.33) * 300.0 + 350.0, sin(iTime * 1.5) * 150.0 + 200.0);

	metaballs[3] = vec2(iMouse.xy);

	float metaball_size[4];

	metaball_size[0] = 1000.0;
	metaball_size[1] = 1000.0;
	metaball_size[2] = 1500.0;
	metaball_size[3] = 2000.0;

	float dist[4];

	float temp1, temp2;

	temp1 = fragCoord.x - metaballs[0].x;
	temp2 = fragCoord.y - metaballs[0].y;
	dist[0] = sqrt(temp1 * temp1 + temp2 * temp2);
	dist[0] = metaball_size[0] / (dist[0] * dist[0]);

	temp1 = fragCoord.x - metaballs[1].x;
	temp2 = fragCoord.y - metaballs[1].y;
	dist[1] = sqrt(temp1 * temp1 + temp2 * temp2);
	dist[1] = metaball_size[1] / (dist[1] * dist[1]);

	temp1 = fragCoord.x - metaballs[2].x;
	temp2 = fragCoord.y - metaballs[2].y;
	dist[2] = sqrt(temp1 * temp1 + temp2 * temp2);
	dist[2] = metaball_size[2] / (dist[2] * dist[2]);

	temp1 = fragCoord.x - metaballs[3].x;
	temp2 = fragCoord.y - metaballs[3].y;
	dist[3] = sqrt(temp1 * temp1 + temp2 * temp2);
	dist[3] = metaball_size[3] / (dist[3] * dist[3]);

	float temp3 = dist[0] + dist[1] + dist[2] + dist[3];
	clamp(temp3, 0.0, 1.0);

	// fragColor = vec4(temp3 * 0.6, temp3 * 0.8, temp3, 1.0);
	fragColor = vec4(temp3, temp3, temp3, 1.0);

}
