float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float r = 0.45;
	vec2 c = vec2(0.5, 0.5);

	float t = iTime+12311.0;
	float l = pow(rand(vec2(sin(floor(uv.x*t*1.0)), sin(floor(uv.y*t*1.452)+1.1513))), 75.0);
	float l2 = pow(rand(vec2(floor(uv.x*t*5.0), floor(uv.y*t*5.0))), 10.0)*3.141592*2.0;

	fragColor.r = fragColor.g = (sin(l2)*0.5+0.5)*l;
	fragColor.b = 0.0;
	fragColor.a = 1.0;

	float d = distance(uv, c) / r;
	if (d > 1.0)
	{
		d = 1.0 / ((d - 1.0) * 250.0);
		fragColor *= d;
	}
	else
	{
		float f = (1.5 - d)*2.0;
		fragColor *= f;
		if (fragColor.r > 1.0) fragColor.r = 1.0;
		if (fragColor.g > 1.0) fragColor.g = 1.0;
		if (fragColor.a > 1.0) fragColor.a = 1.0;
	}
}
