void mainImage(out vec4 c, in vec2 p)
{
    vec2 r = iResolution.xy;
    float h, o = h = 0., b = 1.;

    while (o < 8.)
        h += sin(b / (o + 15.) * (iTime * (o * (.5 - .1 * o++) + 5.) - 28. * length(p / r))) / (b += b);

    vec2 n = 2. * vec2(dFdx(h), dFdy(h));

    c = texture(iChannel0, p / r + n) + dot(n, n) * length(r);
}
