void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 uv=fragCoord.xy*(1./128.0);
	float res=min(iResolution.x,iResolution.y);
	float pixel=1.0/res;
	vec2 p = (fragCoord.xy-iResolution.xy*0.5) * pixel;	
	float h= texture(iChannel0,uv).x;
	float h2=texture(iChannel0,uv+vec2(0.001,0.003)).x;

	float ink=0.0,theta=0.0;
	float rr=res;
	float ofs=0.02+0.00051*iTime+(iMouse.x)*pixel*0.25;
	for (int iter=0;iter<100;++iter) {
		ink+=  max(0.0,1.0-abs(length(p)-0.37)*rr);
		rr/=1.1;
		p*=1.1;
		p.x+=ofs*sin(theta);
		p.y+=ofs*cos(theta);		
		theta+=iTime*0.1;
	}
	ink=sqrt(ink)*0.5;	
	vec3 col = vec3(0.75+(h-h2)*0.2) * smoothstep(1.0,0.0,ink);	
	fragColor=vec4(pow(col*vec3(0.99,0.98,0.97),vec3(0.55)),0.0);

}
