float rand2d(vec2 co,float seed)
{
	return fract(sin(dot(co.xy ,vec2(seed,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uv2 = uv;

    uv += vec2(1. + (sin(iTime * .2) * 0.5),1.); // Pan uv
    uv *= 1.27; // Zoom uv

    //BackGround
    vec3 BackGround = vec3(0.,0.,0.); //BackGround out
    vec2 pos = vec2(0.,0.); //Warping Circle position
    vec2 premod = vec2(0.,0.); //Not Warping Circle position
    float space = 1. / 5.; //Cicles Space

    for(int i = 0;i <5;i++)
    {
        for(int j = 0;j <5 ;j++)
        {
            float fi = float(i); // float iterator
            pos = vec2( fi * 3. * space,float(j) * space); // Circle on a grid
            pos += sin ( rand2d(pos * fi, 55.) * ((iTime)  * 0.01) * 11.6 ) + 1.; // Displacement
            float value = clamp(  tan( 1.7 - length(pos - uv) * 3.)   , 0.,1.) * .0424 ; // Circle tan value
            vec3 Color = vec3(pos.x,pos.y,1. - pos.y); // position to color
            BackGround += value * Color; // output color
        }
    }

    //Lines
    vec3 l = vec3(0.,0.,0.);
    float fi = 0.;
    float Width = .04;
    float WidthVariation = .4;    
    float Intensity = 55.;
    float Contrast =  1.5;
    float Height =  1.75;
    float Speed =  .15;
    float SpeedVariation =  1.01;


    for(int i = 0;i <5 ;i++)
    {
        fi = float(i);


        l += pow( max (Width * ((fi + 1.) * WidthVariation) - abs(uv.y - Height + (sin(uv.x + (iTime * Speed) * (fi * SpeedVariation)) * (0.1 * fi) ) ) ,0.) , Contrast) * Intensity;
    }   

    //Vignet
    float vignet = pow(1. - length(uv2 - vec2(0.5,0.5) ) ,1.5);

    //Composition
    vec4 Composition = vec4(BackGround * vignet + (l * BackGround * 0.3) + (l * 0.5) ,1.0);
	fragColor = Composition;
}
