#define PI 3.1415926
#define BPM 120.
#define RES 1.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(fragCoord.xy*2.-iResolution.xy)/iResolution.y;
    float time=iTime + uv.x * .0095;

    float saw=0.;
    float f=110.*pow(2.,1./12.)*( mod( floor( time*BPM/60. ) , 4.) + 1. );

    for(float c=1.;c<48.;c+=1.)
    {
        float freq = .8 - fract(time*BPM/60.) * .3;
        float cut = pow( freq , log2( c ) );
        cut = cut + sin( pow( cut , 2. ) * PI ) * RES;
        saw += sin( c*PI*f*time ) / c * cut * .2;
    }

    if(abs(saw-uv.y*1.)<.01)
    {
        fragColor = vec4(1.);
    }
    else
    {
		fragColor = vec4(0.);
    }
}

#define PI 3.1415926
#define BPM 120.
#define RES 1.

vec2 mainSound( in int samp,float time)
{
    float saw=0.;
    float f=110.*pow(2.,1./12.)*( mod( floor( time*BPM/60. ) , 4.) + 1. );

    for(float c=1.;c<48.;c+=1.)
    {
        float freq = .8 - fract(time*BPM/60.) * .3;
        float cut = pow( freq , log2( c ) );
        cut = cut + sin( pow( cut , 2. ) * PI ) * RES;
        saw += sin( c*PI*f*time ) / c * cut * .2;
    }

    return vec2( saw );
}
