void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float scale = 0.05;
    float wave = 10.;
    float t =iTime/1.5;
    vec2 offset = sin(uv.yx*wave+t)*scale+sin(uv.yx*wave*vec2(.8,1.2)+t*vec2(2.4,4.4))*scale;
    fragColor = texture(iChannel0, uv+offset);
}
