#define pi 3.1415

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy; //start position
    vec2 v=uv-vec2(0.5);//initial velocity of particle
    v=.1*v;

    //centers of masses
    vec2 mpos=vec2(0.5)+0.1*vec2(sin(iTime/20.),cos(iTime/10.));
    vec2 mpos1=vec2(0.5)+0.3*vec2(sin(iTime/14.),cos(iTime/4.+2.));
    vec2 mpos2=vec2(0.5)+0.5*vec2(sin(iTime/17.+3.),cos(iTime/5.+4.));
    vec2 mpos3=vec2(0.5)+0.6*vec2(sin(iTime/26.+6.),cos(iTime/7.));

    //simulate particle motion for somw timesteps
    for(int i=0;i<20;i++)
    {
    	uv+= v; //apply velocity

        //update velocity by 1/rÂ² to masses
        float l=length(uv-mpos)+.1;
        v=v+0.001*(uv-mpos)/length(uv-mpos)*1./l/l;
        l=length(uv-mpos1)+.1;
        v=v+0.001*(uv-mpos1)/length(uv-mpos1)*1./l/l;
        l=length(uv-mpos2)+.1;
        v=v+0.001*(uv-mpos2)/length(uv-mpos2)*1./l/l;
        l=length(uv-mpos3)+.1;
        v=v+0.001*(uv-mpos3)/length(uv-mpos3)*1./l/l;
        v=0.99*v; //damp velocity
    }

    float s=5.; //scale for intensity
    vec4 texture=vec4(texture(iChannel0, vec2(uv[0]-floor(uv[0]),uv[1]-floor(uv[1]))));
    //color the texture by velocity components
	fragColor = vec4(v[0]*s,v[1]*s,length(v)*s,1.)*length(texture);
//fragColor = vec4(0.5*(sin(atan(v[0],v[1]))+1.),0.,length(v)*s,1.)*length(texture);
}
