//#define ATTRACTOR_PINWHEEL
#define iT iTime
#define CS(a) vec2(cos(a),sin(a))

// m => vec4(center.xy, zoom, max its.);
//   <= vec4(z, dz)
vec4 mandel(in vec2 p, in vec4 m )
{
    vec2 c = (p + m.xy)/m.z,  z = c,  dz = CS(iT);
    for(float j=0.; j<m.w; j++) {
   		mat2 Mz = mat2( z.x, z.y, 
                       -z.y, z.x );
        	 dz = Mz * dz;
         	  z = Mz * z + c;
        if (length(z)>2.) break;
    }
    return vec4(z,dz);
}

void mainImage( out vec4 k, in vec2 p )
{
    vec2 iR = iResolution.xy,
    	  c = vec2(-.25,0);
	p -= .5*iR; p /= iR.y;
	k = mandel(p, vec4(c, .5, iT/3.));

#ifdef ATTRACTOR_PINWHEEL    
	k = k.xyxy/k.zwwz;					// shows space distortion & attractors 
    k = (length(k)<.1) ? vec4(1) : normalize(k);
#else
    k = k*dot(k.yx,k.zw);				// visually more interesting. around 3/4 its.
    k = (length(k)<.1) ? vec4(.5-length(p+c)) : normalize(k);
#endif

}
