// Replace background of specific color with another background.
// Taking idea from the book: Graphics Shader

float uT = 0.5;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 fGroundImage = texture(iChannel0, uv).rgb;
    vec3 bGroundImage = texture(iChannel1, vec2(uv.x, 1.-uv.y)).rgb;

    float r = fGroundImage.r;
    float g = fGroundImage.g;
    float b = fGroundImage.b;
    float rLimit = uT;
    float gLimit = 1. - uT;
    float bLimit = uT;

    vec3 color;

    if(r <= rLimit && g >= gLimit && b < bLimit){
    	color = bGroundImage;	    
    }
    else{
        color = vec3(mix(fGroundImage, bGroundImage, 0.1));
    }

	fragColor = vec4(color,1.0);
}
