#define time iTime

float rand(float seed)
{
    return fract(sin(seed*12.9898)*43758.5453);
}

float wheel(vec2 uv, vec2 p, float r, float s)
{
	float d = length(uv-p);
	float a = atan(p.x-uv.x,p.y-uv.y);
	float s0 = d-r*abs(floor(sin(a*32. + s*time)));
	float s1 = d-.9*r;
    float s2 = -d+.7*r;
	float s3 = d-.8*r*abs(floor(sin(a*8. + s*time/4.)));
	float s4 = d-.3*r;
    float s5 = -d+.1*r;
	return max(s5,min(s4,min(s3,max(s2,min(s1, s0)))));
}

float map(vec2 uv)
{
	float d = 0.67;
	float w0 = wheel(uv, vec2(+d/2.), .5, -15.);
	float w1 = wheel(uv, vec2(-d/2.), .5, +15.);
	return min(w0, w1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = .5 * (2.*fragCoord.xy-iResolution.xy)/iResolution.y;
	float t = 2./iResolution.y;
	float c = smoothstep(-t,t,map(uv))-length(uv)/0.8;
	fragColor = vec4(c) - vec4(.0,.3,.4,.0);
	fragColor *= 1.-rand(time)/2.;
}
