void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 org_uv = uv;

    uv -= 0.5;
    uv.x *= (iResolution.x / iResolution.y);
    uv.y += 0.1;

    uv.y = ((uv.y - (pow(abs(uv.x * (0.88 - uv.y)) + 0.4,1.5)) + 0.2));
    uv.xy /= 0.5;

    float time = mod(iTime, 42.0) * 2000.0;

    float a = abs(sin((time * uv.y) - 4.0 * org_uv.y)) / 2.0 + 0.4;
    float b = pow((sin(iTime*6.0) * org_uv.y * 1.4) * 0.4, 2.0);

    float heart_mask = smoothstep(b + a, b + a + 0.05,length(uv * 2.0));

	fragColor = vec4(vec3(a + b, 0.2 * b, 0.0) - heart_mask, 1.0);
}
