// music : https://soundcloud.com/amerigomusic/fela-soul-itsoweezee

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/iResolution.xy;

    vec3 color = vec3(0);
    vec3 red = vec3(0.99,0.2,0.1);
    float thin = 0.2;

    float map1 = texture(iChannel0, vec2(1.,uv.x)).r;
    float map2 = texture(iChannel0, vec2(0.,uv.x)).r;
    float plotter = thin/(map1 - uv.y/0.2);
    plotter += thin/(map2 - (1.-uv.y)/0.2);

    color = texture(iChannel0, uv).rgb;    
    color = mix(color, red, plotter);

	fragColor = vec4(color,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv*2.-1.;
    float sens = -sign(uv.x);
    uv = mod(abs(uv),1.);
    uv.x = 1. - uv.x;
    float soundX = texture(iChannel1, uv.yy).r;

    vec2 unit = (1.+soundX*2.)/iResolution.xy;
    vec2 uvBuffer = fragCoord.xy / iResolution.xy;
    vec2 offset = vec2(0);
    offset += 3.*vec2(soundX*unit.x*sens, 0);
    vec2 uvTarget = vec2(.5) - uvBuffer;
    float angle = atan(uvTarget.y, uvTarget.x);
    float radius = length(uvTarget);
    radius += soundX*0.2;
    angle += (radius+sin(iTime))*soundX*10.;
    offset += 4.*vec2(cos(angle),sin(angle))*radius*unit;
    uvBuffer += offset;

    vec4 color = texture(iChannel0, uvBuffer);
    vec3 yellow = vec3(0.01,0.02,0.01);
    vec3 red = vec3(0.99,0.2,0.1);
    vec4 soundColor = vec4(mix(yellow, red, smoothstep(0.2,0.8,soundX)),1);
    float should = abs(0.1/(soundX-(1.-uv.x)/0.01));
    color = mix(color, soundColor, clamp(should,0.,1.));
    fragColor = color;
}
