renderpass.code:
// variant of https://www.shadertoy.com/view/ldGSD3
// and https://www.shadertoy.com/view/XsByRt
// ref: http://www.scholarpedia.org/article/Glass_patterns

#define T(U) texture(iChannel0,U)
//#define T(U) step(.9,texture(iChannel0,U))  // for discreet distribution

void mainImage( out vec4 O,  vec2 U )
{
    vec2 R = iResolution.xy;
    U = (U - .5*R) / R.y;
    vec2 M = length(iMouse.xy/R)<.01 ? .01*sin(2.*iTime+vec2(1.6,0)) 
                                     : iMouse.xy/R-.5;
    vec4 T0 =  T( U ),
         T1 =  T( (U/1.05-M) * mat2(sin(.1*sin(2.*iDate.w)+1.57*vec4(1,2,0,1))) );

    if (sin(iTime)>0.) T1 = 1.-T1;

    O = .5 * (T0 + T1);

//    O = sqrt(O);  // gamma correction

}
