void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float w = iResolution.x;
    float h = iResolution.y;

    float aspectRatio = w / h;

    uv.x *= aspectRatio;

    float mx = iMouse.x / w;
    float my = iMouse.y / h;

    float tx = -0.5 * aspectRatio;
    float ty = -0.5;

    mat3 trans = mat3(
        1.0, 0.0, tx,
        0.0, 1.0, ty,
		0.0, 0.0, 1.0
	);

    //float fi = (mx-0.5) * 3.14 * 2.0;
    float fi = iTime * 0.3;

    mat2 rot = mat2(
        cos(fi), -sin(fi),
        sin(fi), cos(fi)
	);

    uv = (vec3(uv,1) * trans).xy;
    uv = uv * rot;
    uv = (vec3(uv,1) * -trans).xy;

    fragColor = texture(iChannel0, uv);
}
