// Water erosion
// by David Hoskins.
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// A very simple slope based erosion simulator.
// It just carves out slopes with 2500 rain drops that evaporate over time.
// It takes a short while to look OK, but not thousands of years! :)
// Water rendered using a type of 'metaball' thing.

#define NUMSQU 25.

// Leaving out the water entirely is quite interesting to watch.
#define RENDERWATER

void mainImage( out vec4 colour, in vec2 coord )
{
	vec2 uv = coord.xy / iResolution.xy;
	colour = texture(iChannel1, coord / iChannelResolution[1].xy);
 #ifdef RENDERWATER
    if (mod(iTime, 4.0) < 3.0)
    {
        float v = 0.0;
      for (float y = 0.; y < NUMSQU; y++)
        {
            for (float x = 0.; x < NUMSQU; x++)
            {
                vec2 xy = vec2(x, y)+.5;
                vec2 pos = texture(iChannel0, xy / iChannelResolution[0].xy).xy;
				v += pow(2. / distance(pos, coord), 2.2);
            }
        }
      	colour = mix(colour, vec4(0.4,.6,1.,1.), min(v, 1.0));
    }
#endif

}

// Water erosion
// by David Hoskins.
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// A very simple slope based erosion simulator.
// It just carves out slopes with 2500 rain drops that evaporate over time.
// It takes a short while to look OK, but not thousands of years! :)

#define NUMSQU 25.0
#define HASHSCALE1 .1031
#define HASHSCALE3 vec3(.1031, .1030, .0973)

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * HASHSCALE3);
    p3 += dot(p3, p3.yzx+19.19);
    return fract(vec2((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y));
}
float terrain(vec2 p)
{
    p+=.5;
    float h = texture(iChannel1, p/ iChannelResolution[1].xy).x;
    return h;
}
vec3 getNormal( in vec2 pos)
{
    vec2  eps = vec2( 1., 0 );
    return normalize( vec3( terrain(pos-eps.xy) - terrain(pos+eps.xy),
                           2.*eps.x,
                 		   terrain(pos-eps.yx) - terrain(pos+eps.yx) ));
}

vec3 placeRainDrop(in vec2 coord)
{
    //vec2 pos = (coord / NUMSQU) *iResolution.xy;
    vec2 pos = hash22(coord-iTime*33.33)*iResolution.xy;
    pos = mod(pos, iResolution.xy);
    float r = iResolution.y;
    return vec3(pos,hash12(coord+iTime)*r*.5+r*.2);
}


void mainImage( out vec4 colour, in vec2 coord )
{

    if (coord.x >= NUMSQU || coord.y >= NUMSQU) discard;

    vec3 pos;    

    if (iFrame <= 1 || iMouse.z > 0.5)
    {
		pos = placeRainDrop(coord);
    }else
    {
        vec4 loc = texture(iChannel0, coord / iChannelResolution[0].xy);
        pos = loc.xyz;
    }
    vec3 nor = getNormal(pos.xy);

    if (pos.z-- < 0.0 || nor.y >= 1.)
    {
     	pos = placeRainDrop(coord);
	 }

	pos.xy = mod(pos.xy+nor.xz*1.0*iResolution.xy, iResolution.xy);

    colour = vec4(pos,clamp(dot(nor.xz, nor.xz),.002,.007));
}

// Water erosion
// by David Hoskins.
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// A very simple slope based erosion simulator.
// It just carves out slopes with 625 rain drops that evaporate over time.
// It takes a short while to look OK, but not thousands of years! :)

#define NUMSQU 25.0
#define HASHSCALE1 .1031

//--------------------------------------------------------------------------
float hash12(in vec2 p, in float scale)
{
    p = mod(p, scale);
	vec3 p3  = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

//--------------------------------------------------------------------------
// Tiled noise to make it wrap around for flying...
const vec2 add = vec2(1,0);
float noise( in vec2 x, in float scale )
{
    x *= scale;
    x+=iTime+20.;
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f*f*(1.5-f)*2.0;

    float res = mix(mix( hash12(p, scale), hash12(p + add.xy, scale),f.x),
                    mix( hash12(p + add.yx, scale), hash12(p + add.xx, scale),f.x),f.y);
    return res;
}
float fbm(vec2 pos)
{

    float a = 0.0;
    float v = 1.;
    float scale = 3.0;
    for (int i = 0; i < 12; i++)
    {
        a+= pow(noise(pos, scale) * v,1.6);
        scale *= 2.2;
        v *= .54;
    }
    return a;
}

void mainImage( out vec4 colour, in vec2 coord )
{
    vec2 uv = coord.xy / iResolution.xy;
    if (iFrame <= 1 || iMouse.z > 0.5)
    {
        float h = fbm(uv);
        colour = vec4(h);
    }else
    {

        colour = texture(iChannel1, uv);
        for (float y = 0.; y < NUMSQU; y++)
        {
            for (float x = 0.; x < NUMSQU; x++)
            {
                vec2 xy = vec2(x, y)+.5;
                vec4 pos = texture(iChannel0, xy / iChannelResolution[0].xy);
                // The speed of the drop is taken as an erosion multiplier...
                float l = clamp(1.3-length(pos.xy-coord), 0.0, 1.0);
                colour -= l*pos.w;
            }
        }
    }
    colour = max(colour,0.0);
}
