void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;
    float intensity = texture(iChannel0, uv).r;

    vec3 redish = vec3(1.0,0.4,0.0);
    vec3 blue = vec3(0.0,0.0,1.0);

    vec3 mixed;

    if(intensity > 0.45)
    {
        mixed = mix(vec3(1.0,1.0,1.0), vec3(1.0,0.4,0.0), (0.85 - (intensity - 0.35)));
    }
    else
    {
        mixed = redish;
    }

    vec3 smoothed = mixed;

    vec3 mulColor = smoothed * intensity * 3.0;
    fragColor = vec4(mulColor,1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float intensity = 0.0;
    float constantShift = 0.07;
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 texel = vec2(1.0,1.0) / iResolution.xy;

    float currentIntensity = texture( iChannel0, uv).r;
    currentIntensity *= 16.0;

    intensity += texture( iChannel0, vec2( uv.x, uv.y - texel.y)).r;
    intensity += texture( iChannel0, vec2( uv.x + texel.x,(uv.y - (texel.y * currentIntensity)))).r;
    intensity += texture( iChannel0, vec2( uv.x - texel.x,(uv.y - (texel.y * currentIntensity)))).r;
    intensity += texture( iChannel0, vec2( uv.x, (uv.y - (texel.y * currentIntensity)))).r;

    intensity /= 4.0 + constantShift;


    fragColor = vec4(intensity,intensity,intensity,1.0);


	if(gl_FragCoord.y < 3.0)
	{
		vec2 noiseUV = vec2( fract(sin(fract(iTime * 23.0)) + uv.x) ,fract(cos(fract(iTime * 2.340)) + uv.y));   
        vec4 noiseCol = texture( iChannel1, noiseUV );

        fragColor = noiseCol;
    }


}
