void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	//uv.y = -1.0 - uv.y;

	uv.x += cos(uv.y*5.2+iTime*1.4)/100.0;
    uv.y += sin(uv.x*5.1+iTime*1.4)/100.0;
	uv.x -= cos(uv.y*5.2+iTime*1.4)/100.0;
	uv.x -= cos(uv.x*5.2+iTime*1.4)/100.0;

	vec4 color = texture(iChannel0, uv);

	fragColor = color;
}
