#define T(y)   texture( iChannel0, ( U + vec2(0,y) ) /R )

void mainImage( out vec4 O,  vec2 U )
{
    vec2 R = iResolution.xy;

    O = T(0.); 

    if (mod(float(iFrame),2.) == 0. && U.y == R.y-1.5) { // --- dust generator on 1st line
        float r, t=iTime;
        for (float x=0.; x<200.; x++) {
            r = 100.*rnd(t+x*37.9141);
            if (r<1.) break;
        }
        O = vec4( .5+floor(R.x*r ) == U.x );   
        return; 
    }

    if (T( 1).x==1.) { O++; return; }    // dust on up cell: transfer here    
    if (T(-1).x==0.)   O -= O; 	         // free space below: dust could be transfered

}
